import React, { useRef } from "react";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

const Carousel = () => {
  const sliderRef = useRef(null); 
  const cardData = [
    {
      image:
        "data:image/jpeg;base64,",
      text: "Columbia Pictures",
    },
    {
      image:
        "data:image/jpeg;base64,",
      text: "Warner Bros Pictures",
    },
    {
      image:
        "data:image/jpeg;base64,",
      text: "Paramount Pictures",
    },
    {
      image:
        "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSzt2ozVG38OluD8kzTyBFt4n2pdMgOk7fl_A&s",
      text: "Universal Pictures",
    },
    {
      image:
        "data:image/jpeg;base64,",
      text: "Columbia Pictures",
    },
    {
      image:
        "data:image/jpeg;base64,",
      text: "Card 2",
    },
    {
      image:
        "data:image/jpeg;base64,",
      text: "Card 3",
    },
    {
      image:
        "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSzt2ozVG38OluD8kzTyBFt4n2pdMgOk7fl_A&s",
      text: "Card 4",
    },
  ];

  const settings = {
    dots: true,
    infinite: true, 
    autoplay: true,
    autoplaySpeed: 3000,
    speed: 500, 
    slidesToShow: 4, 
    slidesToScroll: 1,
    arrows: false, 
    centerMode: false, 
    focusOnSelect: true, 
    responsive: [
      {
        breakpoint: 1024,
        settings: {
          slidesToShow: 3, 
          slidesToScroll: 1,
        },
      },
      {
        breakpoint: 768, 
        settings: {
          slidesToShow: 2,
          slidesToScroll: 1, 
        },
      },
      {
        breakpoint: 480,
        settings: {
          slidesToShow: 1, 
          slidesToScroll: 1, 
        },
      },
    ],
  };

  const handleLeftClick = (e) => {
    const container = e.target.closest(".slick-track");
    const rect = container.getBoundingClientRect();
    const clickX = e.clientX;

    if (clickX < rect.width / 2) {
      if (sliderRef.current) {
        sliderRef.current.slickPrev(); 
      }
    }
  };

  return (
    <div className="w-full px-4 my-8 overflow-hidden">
      {/* Add onClick event to trigger slide move */}
      <div onClick={handleLeftClick} className="relative w-full cursor-pointer">
        <Slider
          {...settings}
          ref={sliderRef}
          className="w-full overflow-hidden"
        >
          {cardData.map((card, index) => (
            <div key={index} className="relative mx-4">
              <img
                src={card.image}
                alt={`card-${index}`}
                className="w-full h-[200px] object-cover rounded-lg"
              />
              <div className=" text-center text-lg font-bold py-2  rounded-md">
                {card.text}
              </div>
            </div>
          ))}
        </Slider>
      </div>
    </div>
  );
};

export default Carousel;
